package fr.creasport.webgestionstock.metier;

public class Taille {
	private int ta_id;
	private String ta_code;
	private String ta_nom;
	
	public Taille() {
		// TODO Auto-generated constructor stub
	}

	public int getTa_id() {
		return ta_id;
	}

	public void setTa_id(int ta_id) {
		this.ta_id = ta_id;
	}

	public String getTa_code() {
		return ta_code;
	}

	public void setTa_code(String ta_code) {
		this.ta_code = ta_code;
	}

	public String getTa_nom() {
		return ta_nom;
	}

	public void setTa_nom(String ta_nom) {
		this.ta_nom = ta_nom;
	}


}
